import Vue from 'vue'
import VueRouter from 'vue-router'
import store from "@/store";

Vue.use(VueRouter)

const routes = [
  {
    path: '/prepare',
    name: 'Prepare',
    component: () => import('../views/Prepare.vue')
  },
  {
    path: '/login',
    name: 'Login',
    component: () => import('../views/Login.vue')
  },
  {
    path: '/404',
    name: '404',
    component: () => import('../views/404.vue')
  },
  {
    path: '/front',
    name: 'Front1',
    component: () => import('../views/front/Front1'),
    children: [

      {
        path: 'Overview',
        name: 'Overview',
        component: () => import('../views/front/Overview.vue')
      },
      {
        path: 'Feature',
        name: 'Feature',
        component: () => import('../views/front/Feature.vue')
      },
      {
        path: 'Celadon',
        name: 'Celadon',
        component: () => import('../views/front/Celadon.vue')
      },
      {
        path: 'Celadon/:name/:location/:intro/:image',
        name: 'Celadon',
        component: () => import('../views/front/Celadon.vue')
      },

      {
        path: 'Liaoning',
        name: 'Liaoning',
        component: () => import('../views/front/Liaoning.vue')
      },




    ]
  },
  {
    path: '/front',
    name: 'Front',
    component: () => import('../views/front/Front'),
    children: [

      {
        path: 'home',
        name: 'FrontHome',
        component: () => import('../views/front/Home.vue')
      },

    ]
  },
]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

// 提供一个重置路由的方法
export const resetRouter = () => {
  router.matcher = new VueRouter({
    mode: 'history',
    base: process.env.BASE_URL,
    routes
  })
}

router.beforeEach((to, from, next) => {
  localStorage.setItem("currentPathName", to.name)  // 设置当前的路由名称
  store.commit("setPath")

  // 未找到路由的情况
  if (!to.matched.length) {
    const storeMenus = localStorage.getItem("menus")
    if (storeMenus) {
      next("/404")
    } else {
      // 跳回登录页面
      next("/login")
    }
  }
  // 其他的情况都放行
  next()

})

export default router
